<template>
  <!-- ✅ UserInfo 아래에 UserSummary 포함 -->
  <div class="container">
    <div class="user-info-block">
      <UserInfo :user="user" @logout="logout" @deleteAccount="deleteAccount" />
      <UserSummary :photos="photos" />
    </div>

    <!-- 2. 중앙 -->
    <MainPhotos
      :mainPhoto="mainPhoto"
      :cameraActive="cameraActive"
      @startCamera="startCamera"
      @handlePhotoUploaded="handlePhotoUploaded"
    />

    <!-- 3. 오른쪽 -->
    <div class="photo-list-section">
      <PhotoList
        :filteredPhotos="filteredPhotos"
        :mainPhotoId="mainPhoto?.id"
        :selectedPhoto="selectedPhoto"
        v-model:selectedDate="selectedDate"  
        :formatTime="formatTime"
        @showPhoto="showPhoto"
        @deletePhoto="deletePhoto"
      />
    </div>

    <!-- 4. 하단 전체 너비 -->
    <div class="full-width">
      <SummaryStats :photos="photos" />
    </div>
  </div>
</template>

<script>
import axios from "axios";
import { ref, onMounted, watch, computed } from "vue";
import { useRouter } from "vue-router";
import UserInfo from '../components/UserInfo.vue';
import PhotoList from '../components/PhotoList.vue';
import MainPhotos from '../components/Mainphotos.vue';
import SummaryStats from '../components/SummaryStats.vue';
import UserSummary from '../components/UserSummary.vue';

export default {
  components: {
    UserInfo,
    PhotoList,
    MainPhotos,
    SummaryStats,
    UserSummary
  },
  setup() {
    const router = useRouter();
    const user = ref({});
    const photos = ref([]);
    const mainPhoto = ref(null);
    const selectedPhoto = ref(null);
    const cameraActive = ref(false);
    const selectedDate = ref("");

    const filteredPhotos = computed(() => {
      if (!selectedDate.value) return photos.value;
      return photos.value.filter(photo => {
        const dateOnly = new Date(photo.uploaded_at).toISOString().split("T")[0];
        return dateOnly === selectedDate.value;
      });
    });

    const formatTime = (datetime) => {
      const date = new Date(datetime);
      const month = (date.getMonth() + 1).toString().padStart(2, '0');
      const day = date.getDate().toString().padStart(2, '0');
      const hours = date.getHours().toString().padStart(2, '0');
      const minutes = date.getMinutes().toString().padStart(2, '0');
      return `${month}월 ${day}일 ${hours}:${minutes}`;
    };

    watch(photos, (newVal) => {
      console.log("📸 사진 목록이 변경되었습니다:", newVal);
    });

    const fetchUser = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) return console.error("❌ 액세스 토큰 없음");

        const decodedToken = JSON.parse(atob(token.split(".")[1]));
        const email = decodedToken.email;

        const res = await axios.get(`http://210.101.236.158:5000/api/user/me?email=${email}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (res.data.success) {
          user.value = res.data.user;
          await fetchPhotos();
        } else {
          console.error("❌ 사용자 정보 가져오기 실패:", res.data.message);
        }
      } catch (err) {
        console.error("❌ 사용자 정보를 보내오는 중 오류:", err);
      }
    };

    const fetchPhotos = async () => {
      if (!user.value.id) return console.error("❌ user_id가 없음");
      try {
        const res = await axios.get(`http://210.101.236.158:5000/api/photos?user_id=${user.value.id}`);
        photos.value = res.data;
        mainPhoto.value = photos.value.length > 0 ? photos.value[0] : null;
      } catch (err) {
        console.error("🚨 사진 목록을 보내오는 중 오류:", err);
      }
    };

    const handlePhotoUploaded = async () => {
      if (!user.value.id) return;
      setTimeout(async () => {
        try {
          const res = await axios.get(`http://210.101.236.158:5000/api/photos?user_id=${user.value.id}`);
          photos.value = res.data;
          mainPhoto.value = photos.value.length > 0 ? photos.value[0] : null;
        } catch (err) {
          console.error("🚨 사진 목록 새로고침 실패:", err);
        }
      }, 1000);
    };

    const deletePhoto = async (id) => {
      if (id === mainPhoto.value.id) return alert("대표 사진은 삭제할 수 없습니다.");
      try {
        const res = await axios.delete(`http://210.101.236.158:5000/api/photos/${id}`);
        if (res.data.success) {
          alert("사진이 삭제되었습니다.");
          fetchPhotos();
        } else {
          console.error("🚨 사진 삭제 실패:", res.data.message);
        }
      } catch (err) {
        console.error("🚨 사진 삭제 중 오류:", err);
      }
    };

    const showPhoto = (photo) => {
      selectedPhoto.value = photo;
    };

    const deleteAccount = async () => {
      if (confirm("정말 회원 탈퇴를 진행하시겠습니꽃")) {
        try {
          const res = await axios.delete(`http://210.101.236.158:5000/api/user/delete/${user.value.id}`);
          if (res.data.success) {
            localStorage.removeItem("token");
            alert("회원 탈퇴가 완료되었습니다.");
            router.push("/login");
          } else {
            alert("회원 탈퇴에 실패했습니다.");
          }
        } catch (err) {
          alert("회원 탈퇴 중 문제가 발생했습니다.");
        }
      }
    };

    const logout = () => {
      localStorage.removeItem("token");
      router.push("/login");
    };

    const startCamera = () => {
      cameraActive.value = true;
    };

    onMounted(fetchUser);

    return {
      user,
      photos,
      mainPhoto,
      selectedPhoto,
      uploadPhoto: () => {},
      deletePhoto,
      showPhoto,
      deleteAccount,
      logout,
      startCamera,
      cameraActive,
      handlePhotoUploaded,
      formatTime,
      selectedDate,
      filteredPhotos
    };
  }
};
</script>

